mapper.select:

FROM AUTH_AUTHORIZATION.CAS_ORG
WHERE DELETED = 'F'

    AND COMPANY_ID = #{companyId,jdbcType=BIGINT}

SELECT ID id, QUARTERS_DESC quartersDesc, DELETED deleted, COMPANY_ID companyId FROM `hcm`.`quarters_hcm`
QUARTERS_DESC like CONCAT('%',#{quartersDesc,jdbcType=VARCHAR},'%')
and COMPANY_ID = #{companyId,jdbcType=BIGINT}
and deleted = #{deleted,jdbcType=VARCHAR}
order by id desc

SELECT a.ID id, a.ORG_CODE orgCode, a.ORG_NAME orgName, a.PARENT_ID parentId, a.IS_LEAF isLeaf, a.DESCRIPTION description, a.SECONDLEADER secondleader, a.LEADER leader, a.HIRES hires, a.ORG_ID_PATH orgIdPath, a.ORG_PATH orgPath, a.ORDERS orders, a.DELETED deleted, a.COMPANY_ID companyId, a.CREATE_TIME createTime, a.CREATE_BY createBy, a.MODIFY_TIME modifyTime, a.MODIFY_BY modifyBy FROM AUTH_AUTHORIZATION.CAS_ORG a LEFT JOIN hcm.CONTACT_ORG_TO_EMP b ON b.ORG_ID = a.ID LEFT JOIN hcm.EMP_FOLDER_HCM c ON b.EMP_FOLDER_ID = c.ID LEFT JOIN auth_authorization.cas_user d ON c.EMP_NO = d.EMP_NO WHERE a.DELETED = 'F' AND b.DELETED = 'F' AND c.DELETED = 'F'
AND a.COMPANY_ID = #{companyId,jdbcType=BIGINT}
AND d.ID = #{userId,jdbcType=BIGINT}
