mapper.update:
UPDATE CUSTOMS_CRM
CUSTOM_NO = #{customNo,jdbcType=VARCHAR}, CUSTOM_NAME = #{customName,jdbcType=VARCHAR}, PIPING_TYPE_ID = #{pipingTypeId,jdbcType=BIGINT}, CUSTOM_TYPE = #{customType,jdbcType=VARCHAR}, TRADE = #{trade,jdbcType=VARCHAR}, ADDRESS = #{address,jdbcType=VARCHAR}, SCALE = #{scale,jdbcType=VARCHAR}, INVEST_STRUCT = #{investStruct,jdbcType=VARCHAR}, EMPLOYEES = #{employees,jdbcType=INTEGER}, REGISITER_DATE = #{regisiterDate,jdbcType=TIMESTAMP}, BOSS_NAME = #{bossName,jdbcType=VARCHAR}, ACT_POWER = #{actPower,jdbcType=VARCHAR}, LICENSE_NO = #{licenseNo,jdbcType=VARCHAR}, TAXATION_NO = #{taxationNo,jdbcType=VARCHAR}, DEAL_RANGE = #{dealRange,jdbcType=VARCHAR}, IS_CREDIT = #{isCredit,jdbcType=TINYINT}, WARING_TIME = #{waringTime,jdbcType=INTEGER}, DELETED = #{deleted,jdbcType=VARCHAR}, COMPANY_ID = #{companyId,jdbcType=BIGINT}, CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}, CREATE_BY = #{createBy,jdbcType=VARCHAR}, MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}, MODIFY_BY = #{modifyBy,jdbcType=VARCHAR},
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE CUSTOMS_CRM
   SET DELETED     = #{deleted,jdbcType=VARCHAR},
       MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
       MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
 WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:

 FROM CUSTOMS_CRM
WHERE ID = #{id,jdbcType=INTEGER}
  AND DELETED = #{deleted,jdbcType=VARCHAR}


  FROM CUSTOMS_CRM


		AND ID = #{id,jdbcType=INTEGER}


		AND CUSTOM_NO = #{customNo,jdbcType=VARCHAR}


