mapper.update:
UPDATE DELIVERY_DETAIL_CRM
VOUCHER_NO = #{voucherNo,jdbcType=VARCHAR}, ORDER_NO = #{orderNo,jdbcType=VARCHAR}, WARES_NO = #{waresNo,jdbcType=VARCHAR}, AMOUNT = #{amount,jdbcType=DECIMAL}, PRICE = #{price,jdbcType=DECIMAL}, UNIT = #{unit,jdbcType=VARCHAR}, TOTAL = #{total,jdbcType=DECIMAL}, REMARK = #{remark,jdbcType=VARCHAR}, DELETED = #{deleted,jdbcType=VARCHAR}, COMPANY_ID = #{companyId,jdbcType=BIGINT}, CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}, CREATE_BY = #{createBy,jdbcType=VARCHAR}, MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}, MODIFY_BY = #{modifyBy,jdbcType=VARCHAR},
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE DELIVERY_DETAIL_CRM
   SET DELETED     = #{deleted,jdbcType=VARCHAR},
       MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
       MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
 WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:

 FROM DELIVERY_DETAIL_CRM
WHERE ID = #{id,jdbcType=INTEGER}
  AND DELETED = #{deleted,jdbcType=VARCHAR}


  FROM DELIVERY_DETAIL_CRM


		AND ID = #{id,jdbcType=INTEGER}


		AND VOUCHER_NO = #{voucherNo,jdbcType=VARCHAR}


		AND ORDER_NO = #{orderNo,jdbcType=VARCHAR}


		AND WARES_NO = #{waresNo,jdbcType=VARCHAR}


		AND AMOUNT = #{amount,jdbcType=DECIMAL}


		AND PRICE = #{price,jdbcType=DECIMAL}


		AND UNIT = #{unit,jdbcType=VARCHAR}


		AND TOTAL = #{total,jdbcType=DECIMAL}


		AND REMARK = #{remark,jdbcType=VARCHAR}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}


  FROM DELIVERY_DETAIL_CRM


		AND ID = #{id,jdbcType=INTEGER}


		AND VOUCHER_NO = #{voucherNo,jdbcType=VARCHAR}


		AND ORDER_NO = #{orderNo,jdbcType=VARCHAR}


		AND WARES_NO = #{waresNo,jdbcType=VARCHAR}


		AND AMOUNT = #{amount,jdbcType=DECIMAL}


		AND PRICE = #{price,jdbcType=DECIMAL}


		AND UNIT = #{unit,jdbcType=VARCHAR}


		AND TOTAL = #{total,jdbcType=DECIMAL}


		AND REMARK = #{remark,jdbcType=VARCHAR}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}

 WHERE DELETED = "F"
