mapper.update:
UPDATE INVENTORY_MATTERS_IOS
PERIODS = #{periods,jdbcType=VARCHAR}, STORE_SET_ID = #{storeSetId,jdbcType=BIGINT}, MATTERS_NO = #{mattersNo,jdbcType=VARCHAR}, START_DATE = #{startDate,jdbcType=TIMESTAMP}, END_DATE = #{endDate,jdbcType=TIMESTAMP}, THE_AMOUNT = #{theAmount,jdbcType=DECIMAL}, SHO_AMOUNT = #{shoAmount,jdbcType=DECIMAL}, UNIT = #{unit,jdbcType=VARCHAR}, DESC = #{desc,jdbcType=VARCHAR}, DELETED = #{deleted,jdbcType=VARCHAR}, COMPANY_ID = #{companyId,jdbcType=BIGINT}, CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}, CREATE_BY = #{createBy,jdbcType=VARCHAR}, MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}, MODIFY_BY = #{modifyBy,jdbcType=VARCHAR},
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE INVENTORY_MATTERS_IOS
   SET DELETED     = #{deleted,jdbcType=VARCHAR},
       MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
       MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
 WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:

 FROM INVENTORY_MATTERS_IOS
WHERE ID = #{id,jdbcType=INTEGER}
  AND DELETED = #{deleted,jdbcType=VARCHAR}


  FROM INVENTORY_MATTERS_IOS


		AND ID = #{id,jdbcType=INTEGER}


		AND PERIODS = #{periods,jdbcType=VARCHAR}


		AND STORE_SET_ID = #{storeSetId,jdbcType=BIGINT}


		AND MATTERS_NO = #{mattersNo,jdbcType=VARCHAR}


		AND START_DATE = #{startDate,jdbcType=TIMESTAMP}


		AND END_DATE = #{endDate,jdbcType=TIMESTAMP}


		AND THE_AMOUNT = #{theAmount,jdbcType=DECIMAL}


		AND SHO_AMOUNT = #{shoAmount,jdbcType=DECIMAL}


		AND UNIT = #{unit,jdbcType=VARCHAR}


		AND DESC = #{desc,jdbcType=VARCHAR}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}


  FROM INVENTORY_MATTERS_IOS


		AND ID = #{id,jdbcType=INTEGER}


		AND PERIODS = #{periods,jdbcType=VARCHAR}


		AND STORE_SET_ID = #{storeSetId,jdbcType=BIGINT}


		AND MATTERS_NO = #{mattersNo,jdbcType=VARCHAR}


		AND START_DATE = #{startDate,jdbcType=TIMESTAMP}


		AND END_DATE = #{endDate,jdbcType=TIMESTAMP}


		AND THE_AMOUNT = #{theAmount,jdbcType=DECIMAL}


		AND SHO_AMOUNT = #{shoAmount,jdbcType=DECIMAL}


		AND UNIT = #{unit,jdbcType=VARCHAR}


		AND DESC = #{desc,jdbcType=VARCHAR}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}

 WHERE DELETED = "F"
