mapper.update:
UPDATE MID_PROD_OUT_IOS
VOUCHER_NO = #{voucherNo,jdbcType=VARCHAR}, IN_DATE = #{inDate,jdbcType=TIMESTAMP}, CAS_ORG_ID = #{casOrgId,jdbcType=BIGINT}, USE_EMP_NO = #{useEmpNo,jdbcType=VARCHAR}, STROE_SET_ID = #{stroeSetId,jdbcType=BIGINT}, STROE_EMP = #{stroeEmp,jdbcType=VARCHAR}, OUT_TYPE = #{outType,jdbcType=VARCHAR}, REMARK = #{remark,jdbcType=VARCHAR}, DELETED = #{deleted,jdbcType=VARCHAR}, COMPANY_ID = #{companyId,jdbcType=BIGINT}, CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}, CREATE_BY = #{createBy,jdbcType=VARCHAR}, MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}, MODIFY_BY = #{modifyBy,jdbcType=VARCHAR},
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE MID_PROD_OUT_IOS
   SET DELETED     = #{deleted,jdbcType=VARCHAR},
       MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
       MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
 WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:

 FROM MID_PROD_OUT_IOS
WHERE ID = #{id,jdbcType=INTEGER}
  AND DELETED = #{deleted,jdbcType=VARCHAR}


  FROM MID_PROD_OUT_IOS


		AND ID = #{id,jdbcType=INTEGER}


		AND VOUCHER_NO = #{voucherNo,jdbcType=VARCHAR}


		AND IN_DATE = #{inDate,jdbcType=TIMESTAMP}


		AND CAS_ORG_ID = #{casOrgId,jdbcType=BIGINT}


		AND USE_EMP_NO = #{useEmpNo,jdbcType=VARCHAR}


		AND STROE_SET_ID = #{stroeSetId,jdbcType=BIGINT}


		AND STROE_EMP = #{stroeEmp,jdbcType=VARCHAR}


		AND OUT_TYPE = #{outType,jdbcType=VARCHAR}


		AND REMARK = #{remark,jdbcType=VARCHAR}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}


  FROM MID_PROD_OUT_IOS


		AND ID = #{id,jdbcType=INTEGER}


		AND VOUCHER_NO = #{voucherNo,jdbcType=VARCHAR}


		AND IN_DATE = #{inDate,jdbcType=TIMESTAMP}


		AND CAS_ORG_ID = #{casOrgId,jdbcType=BIGINT}


		AND USE_EMP_NO = #{useEmpNo,jdbcType=VARCHAR}


		AND STROE_SET_ID = #{stroeSetId,jdbcType=BIGINT}


		AND STROE_EMP = #{stroeEmp,jdbcType=VARCHAR}


		AND OUT_TYPE = #{outType,jdbcType=VARCHAR}


		AND REMARK = #{remark,jdbcType=VARCHAR}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}

 WHERE DELETED = "F"
