mapper.update:
UPDATE ORDER_PICT_MES
INSIDE_ORDER = #{insideOrder,jdbcType=VARCHAR}, PROD_NO = #{prodNo,jdbcType=VARCHAR}, CRAFWORK_ID = #{crafworkId,jdbcType=BIGINT}, SERIAL = #{serial,jdbcType=INTEGER}, PICTURES = #{pictures,jdbcType=unkown}, FILE_PATH = #{filePath,jdbcType=VARCHAR}, LINK_PATH = #{linkPath,jdbcType=VARCHAR}, DESC = #{desc,jdbcType=VARCHAR}, SEND_EMP = #{sendEmp,jdbcType=VARCHAR}, SEND_DATE = #{sendDate,jdbcType=TIMESTAMP}, DELETED = #{deleted,jdbcType=VARCHAR}, COMPANY_ID = #{companyId,jdbcType=BIGINT}, CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}, CREATE_BY = #{createBy,jdbcType=VARCHAR}, MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}, MODIFY_BY = #{modifyBy,jdbcType=VARCHAR},
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE ORDER_PICT_MES
   SET DELETED     = #{deleted,jdbcType=VARCHAR},
       MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
       MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
 WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:

 FROM ORDER_PICT_MES
WHERE ID = #{id,jdbcType=INTEGER}
  AND DELETED = #{deleted,jdbcType=VARCHAR}


  FROM ORDER_PICT_MES


		AND ID = #{id,jdbcType=INTEGER}


		AND INSIDE_ORDER = #{insideOrder,jdbcType=VARCHAR}


		AND PROD_NO = #{prodNo,jdbcType=VARCHAR}


		AND CRAFWORK_ID = #{crafworkId,jdbcType=BIGINT}


		AND SERIAL = #{serial,jdbcType=INTEGER}


		AND PICTURES = #{pictures,jdbcType=unkown}


		AND FILE_PATH = #{filePath,jdbcType=VARCHAR}


		AND LINK_PATH = #{linkPath,jdbcType=VARCHAR}


		AND DESC = #{desc,jdbcType=VARCHAR}


		AND SEND_EMP = #{sendEmp,jdbcType=VARCHAR}


		AND SEND_DATE = #{sendDate,jdbcType=TIMESTAMP}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}


  FROM ORDER_PICT_MES


		AND ID = #{id,jdbcType=INTEGER}


		AND INSIDE_ORDER = #{insideOrder,jdbcType=VARCHAR}


		AND PROD_NO = #{prodNo,jdbcType=VARCHAR}


		AND CRAFWORK_ID = #{crafworkId,jdbcType=BIGINT}


		AND SERIAL = #{serial,jdbcType=INTEGER}


		AND PICTURES = #{pictures,jdbcType=unkown}


		AND FILE_PATH = #{filePath,jdbcType=VARCHAR}


		AND LINK_PATH = #{linkPath,jdbcType=VARCHAR}


		AND DESC = #{desc,jdbcType=VARCHAR}


		AND SEND_EMP = #{sendEmp,jdbcType=VARCHAR}


		AND SEND_DATE = #{sendDate,jdbcType=TIMESTAMP}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}

 WHERE DELETED = "F"
