mapper.update:
UPDATE PROCESS_PICT_MES
ORDER_NO = #{orderNo,jdbcType=VARCHAR}, CUSTOM_NO = #{customNo,jdbcType=VARCHAR}, PROD_NO = #{prodNo,jdbcType=VARCHAR}, CRAFWORK_ID = #{crafworkId,jdbcType=BIGINT}, SERIAL = #{serial,jdbcType=INTEGER}, PICTURES = #{pictures,jdbcType=VARCHAR}, FILE_NAME = #{fileName,jdbcType=VARCHAR}, SEND_EMP = #{sendEmp,jdbcType=VARCHAR}, SENT_DATE = #{sentDate,jdbcType=TIMESTAMP}, DELETED = #{deleted,jdbcType=VARCHAR}, COMPANY_ID = #{companyId,jdbcType=BIGINT}, CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}, CREATE_BY = #{createBy,jdbcType=VARCHAR}, MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}, MODIFY_BY = #{modifyBy,jdbcType=VARCHAR},
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE PROCESS_PICT_MES
   SET DELETED     = #{deleted,jdbcType=VARCHAR},
       MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
       MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
 WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:

 FROM PROCESS_PICT_MES
WHERE ID = #{id,jdbcType=INTEGER}
  AND DELETED = #{deleted,jdbcType=VARCHAR}


  FROM PROCESS_PICT_MES


		AND ID = #{id,jdbcType=INTEGER}


		AND ORDER_NO = #{orderNo,jdbcType=VARCHAR}


		AND CUSTOM_NO = #{customNo,jdbcType=VARCHAR}


		AND PROD_NO = #{prodNo,jdbcType=VARCHAR}


		AND CRAFWORK_ID = #{crafworkId,jdbcType=BIGINT}


		AND SERIAL = #{serial,jdbcType=INTEGER}


		AND PICTURES = #{pictures,jdbcType=VARCHAR}


		AND FILE_NAME = #{fileName,jdbcType=VARCHAR}


		AND SEND_EMP = #{sendEmp,jdbcType=VARCHAR}


		AND SENT_DATE = #{sentDate,jdbcType=TIMESTAMP}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}


  FROM PROCESS_PICT_MES


		AND ID = #{id,jdbcType=INTEGER}


		AND ORDER_NO = #{orderNo,jdbcType=VARCHAR}


		AND CUSTOM_NO = #{customNo,jdbcType=VARCHAR}


		AND PROD_NO = #{prodNo,jdbcType=VARCHAR}


		AND CRAFWORK_ID = #{crafworkId,jdbcType=BIGINT}


		AND SERIAL = #{serial,jdbcType=INTEGER}


		AND PICTURES = #{pictures,jdbcType=VARCHAR}


		AND FILE_NAME = #{fileName,jdbcType=VARCHAR}


		AND SEND_EMP = #{sendEmp,jdbcType=VARCHAR}


		AND SENT_DATE = #{sentDate,jdbcType=TIMESTAMP}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}

 WHERE DELETED = "F"
