mapper.update:
UPDATE PROD_CRAFWORK_PLM
PROD_NO = #{prodNo,jdbcType=VARCHAR}, MID_PROD_NO = #{midProdNo,jdbcType=VARCHAR}, CRAFWORK_ID = #{crafworkId,jdbcType=BIGINT}, CRAFWORK_DESC = #{crafworkDesc,jdbcType=VARCHAR}, PRICE = #{price,jdbcType=DECIMAL}, QUARTERS_EMP = #{quartersEmp,jdbcType=VARCHAR}, MODEL_NO = #{modelNo,jdbcType=VARCHAR}, BEF_CRAFWORK = #{befCrafwork,jdbcType=VARCHAR}, CARF_SEQ = #{carfSeq,jdbcType=BIGINT}, AMOUNT = #{amount,jdbcType=DECIMAL}, UNIT = #{unit,jdbcType=VARCHAR}, MAC_MINUTES = #{macMinutes,jdbcType=INTEGER}, EMP_MINUTES = #{empMinutes,jdbcType=INTEGER}, IS_CHECK = #{isCheck,jdbcType=TINYINT}, DAY_AMOUNT = #{dayAmount,jdbcType=DECIMAL}, REMARK = #{remark,jdbcType=VARCHAR}, DELETED = #{deleted,jdbcType=VARCHAR}, COMPANY_ID = #{companyId,jdbcType=BIGINT}, CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}, CREATE_BY = #{createBy,jdbcType=VARCHAR}, MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}, MODIFY_BY = #{modifyBy,jdbcType=VARCHAR},
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE PROD_CRAFWORK_PLM
   SET DELETED     = #{deleted,jdbcType=VARCHAR},
       MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
       MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
 WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:

 FROM PROD_CRAFWORK_PLM
WHERE ID = #{id,jdbcType=INTEGER}
  AND DELETED = #{deleted,jdbcType=VARCHAR}


  FROM PROD_CRAFWORK_PLM


		AND ID = #{id,jdbcType=INTEGER}


