		AND BEF_CRAFWORK = #{befCrafwork,jdbcType=VARCHAR}


		AND CARF_SEQ = #{carfSeq,jdbcType=BIGINT}


		AND AMOUNT = #{amount,jdbcType=DECIMAL}


