mapper.update:
UPDATE PROD_IN_DETAIL_IOS
VOUCHER_NO = #{voucherNo,jdbcType=VARCHAR}, PROD_NO = #{prodNo,jdbcType=VARCHAR}, MAKE_DATE = #{makeDate,jdbcType=TIMESTAMP}, BATCH = #{batch,jdbcType=VARCHAR}, MAKE_PLAN_ID = #{makePlanId,jdbcType=BIGINT}, IN_COST = #{inCost,jdbcType=DECIMAL}, RACK_POSITION = #{rackPosition,jdbcType=VARCHAR}, MAKE_AMOUNT = #{makeAmount,jdbcType=DECIMAL}, AMOUNT = #{amount,jdbcType=DECIMAL}, UNIT = #{unit,jdbcType=VARCHAR}, REMARK = #{remark,jdbcType=VARCHAR}, DELETED = #{deleted,jdbcType=VARCHAR}, COMPANY_ID = #{companyId,jdbcType=BIGINT}, CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}, CREATE_BY = #{createBy,jdbcType=VARCHAR}, MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}, MODIFY_BY = #{modifyBy,jdbcType=VARCHAR},
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE PROD_IN_DETAIL_IOS
   SET DELETED     = #{deleted,jdbcType=VARCHAR},
       MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
       MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
 WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:

 FROM PROD_IN_DETAIL_IOS
WHERE ID = #{id,jdbcType=INTEGER}
  AND DELETED = #{deleted,jdbcType=VARCHAR}


  FROM PROD_IN_DETAIL_IOS


		AND ID = #{id,jdbcType=INTEGER}


		AND VOUCHER_NO = #{voucherNo,jdbcType=VARCHAR}


		AND PROD_NO = #{prodNo,jdbcType=VARCHAR}


		AND MAKE_DATE = #{makeDate,jdbcType=TIMESTAMP}


		AND BATCH = #{batch,jdbcType=VARCHAR}


		AND MAKE_PLAN_ID = #{makePlanId,jdbcType=BIGINT}


		AND IN_COST = #{inCost,jdbcType=DECIMAL}


		AND RACK_POSITION = #{rackPosition,jdbcType=VARCHAR}


		AND MAKE_AMOUNT = #{makeAmount,jdbcType=DECIMAL}


		AND AMOUNT = #{amount,jdbcType=DECIMAL}


		AND UNIT = #{unit,jdbcType=VARCHAR}


		AND REMARK = #{remark,jdbcType=VARCHAR}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}


  FROM PROD_IN_DETAIL_IOS


		AND ID = #{id,jdbcType=INTEGER}


		AND VOUCHER_NO = #{voucherNo,jdbcType=VARCHAR}


		AND PROD_NO = #{prodNo,jdbcType=VARCHAR}


		AND MAKE_DATE = #{makeDate,jdbcType=TIMESTAMP}


		AND BATCH = #{batch,jdbcType=VARCHAR}


		AND MAKE_PLAN_ID = #{makePlanId,jdbcType=BIGINT}


		AND IN_COST = #{inCost,jdbcType=DECIMAL}


		AND RACK_POSITION = #{rackPosition,jdbcType=VARCHAR}


		AND MAKE_AMOUNT = #{makeAmount,jdbcType=DECIMAL}


		AND AMOUNT = #{amount,jdbcType=DECIMAL}


		AND UNIT = #{unit,jdbcType=VARCHAR}


		AND REMARK = #{remark,jdbcType=VARCHAR}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}

 WHERE DELETED = "F"
