mapper.update:
UPDATE PRODUCE_ORDER_APS
VOUCHER_NO = #{voucherNo,jdbcType=VARCHAR}, INSIDE_ORDER = #{insideOrder,jdbcType=VARCHAR}, ORDER_DATE = #{orderDate,jdbcType=TIMESTAMP}, ORG_ID = #{orgId,jdbcType=BIGINT}, DELETED = #{deleted,jdbcType=VARCHAR}, COMPANY_ID = #{companyId,jdbcType=BIGINT}, CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}, CREATE_BY = #{createBy,jdbcType=VARCHAR}, MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}, MODIFY_BY = #{modifyBy,jdbcType=VARCHAR},
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE PRODUCE_ORDER_APS
   SET DELETED     = #{deleted,jdbcType=VARCHAR},
       MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
       MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
 WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:

 FROM PRODUCE_ORDER_APS
WHERE ID = #{id,jdbcType=INTEGER}
  AND DELETED = #{deleted,jdbcType=VARCHAR}


  FROM PRODUCE_ORDER_APS


		AND ID = #{id,jdbcType=INTEGER}


		AND VOUCHER_NO = #{voucherNo,jdbcType=VARCHAR}


		AND INSIDE_ORDER = #{insideOrder,jdbcType=VARCHAR}


		AND ORDER_DATE = #{orderDate,jdbcType=TIMESTAMP}


		AND ORG_ID = #{orgId,jdbcType=BIGINT}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}


  FROM PRODUCE_ORDER_APS


		AND ID = #{id,jdbcType=INTEGER}


		AND VOUCHER_NO = #{voucherNo,jdbcType=VARCHAR}


		AND INSIDE_ORDER = #{insideOrder,jdbcType=VARCHAR}


		AND ORDER_DATE = #{orderDate,jdbcType=TIMESTAMP}


		AND ORG_ID = #{orgId,jdbcType=BIGINT}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}

 WHERE DELETED = "F"
