mapper.update:
UPDATE TASK_POND_MES
INSIDE_ORDER = #{insideOrder,jdbcType=VARCHAR}, PROD_NO = #{prodNo,jdbcType=VARCHAR}, CRAFWORK_ID = #{crafworkId,jdbcType=BIGINT}, AMOUNT = #{amount,jdbcType=DECIMAL}, PLAN_STARTDATE = #{planStartdate,jdbcType=TIMESTAMP}, PLAN_HOURS = #{planHours,jdbcType=DECIMAL}, PRICE = #{price,jdbcType=DECIMAL}, STATUS = #{status,jdbcType=VARCHAR}, DESC = #{desc,jdbcType=VARCHAR}, DELETED = #{deleted,jdbcType=VARCHAR}, COMPANY_ID = #{companyId,jdbcType=BIGINT}, CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}, CREATE_BY = #{createBy,jdbcType=VARCHAR}, MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}, MODIFY_BY = #{modifyBy,jdbcType=VARCHAR},
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE TASK_POND_MES
   SET DELETED     = #{deleted,jdbcType=VARCHAR},
       MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
       MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
 WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:

 FROM TASK_POND_MES
WHERE ID = #{id,jdbcType=INTEGER}
  AND DELETED = #{deleted,jdbcType=VARCHAR}


  FROM TASK_POND_MES


		AND ID = #{id,jdbcType=INTEGER}


		AND INSIDE_ORDER = #{insideOrder,jdbcType=VARCHAR}


		AND PROD_NO = #{prodNo,jdbcType=VARCHAR}


		AND CRAFWORK_ID = #{crafworkId,jdbcType=BIGINT}


		AND AMOUNT = #{amount,jdbcType=DECIMAL}


		AND PLAN_STARTDATE = #{planStartdate,jdbcType=TIMESTAMP}


		AND PLAN_HOURS = #{planHours,jdbcType=DECIMAL}


		AND PRICE = #{price,jdbcType=DECIMAL}


		AND STATUS = #{status,jdbcType=VARCHAR}


		AND DESC = #{desc,jdbcType=VARCHAR}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}


  FROM TASK_POND_MES


		AND ID = #{id,jdbcType=INTEGER}


		AND INSIDE_ORDER = #{insideOrder,jdbcType=VARCHAR}


		AND PROD_NO = #{prodNo,jdbcType=VARCHAR}


		AND CRAFWORK_ID = #{crafworkId,jdbcType=BIGINT}


		AND AMOUNT = #{amount,jdbcType=DECIMAL}


		AND PLAN_STARTDATE = #{planStartdate,jdbcType=TIMESTAMP}


		AND PLAN_HOURS = #{planHours,jdbcType=DECIMAL}


		AND PRICE = #{price,jdbcType=DECIMAL}


		AND STATUS = #{status,jdbcType=VARCHAR}


		AND DESC = #{desc,jdbcType=VARCHAR}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}

 WHERE DELETED = "F"
