mapper.update:
UPDATE TASK_RECEIVE_EMP_MES
INSIDE_ORDER = #{insideOrder,jdbcType=VARCHAR}, PROD_NO = #{prodNo,jdbcType=VARCHAR}, CRAFWORK_ID = #{crafworkId,jdbcType=BIGINT}, DO_EMP_NO = #{doEmpNo,jdbcType=VARCHAR}, ACT_DATE = #{actDate,jdbcType=TIMESTAMP}, PLAN_DATE = #{planDate,jdbcType=TIMESTAMP}, ACT_HOURS = #{actHours,jdbcType=DECIMAL}, AMOUNT = #{amount,jdbcType=DECIMAL}, STATUS = #{status,jdbcType=VARCHAR}, CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}, CREATE_BY = #{createBy,jdbcType=VARCHAR}, MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}, MODIFY_BY = #{modifyBy,jdbcType=VARCHAR}, COMPANY_ID = #{companyId,jdbcType=BIGINT}, DELETED = #{deleted,jdbcType=VARCHAR}, PRICE = #{price,jdbcType=DECIMAL},
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE TASK_RECEIVE_EMP_MES
   SET DELETED     = #{deleted,jdbcType=VARCHAR},
       MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
       MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
 WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:

 FROM TASK_RECEIVE_EMP_MES
WHERE ID = #{id,jdbcType=INTEGER}
  AND DELETED = #{deleted,jdbcType=VARCHAR}


  FROM TASK_RECEIVE_EMP_MES


		AND ID = #{id,jdbcType=INTEGER}


		AND INSIDE_ORDER = #{insideOrder,jdbcType=VARCHAR}


		AND PROD_NO = #{prodNo,jdbcType=VARCHAR}


		AND CRAFWORK_ID = #{crafworkId,jdbcType=BIGINT}


		AND DO_EMP_NO = #{doEmpNo,jdbcType=VARCHAR}


		AND ACT_DATE = #{actDate,jdbcType=TIMESTAMP}


		AND PLAN_DATE = #{planDate,jdbcType=TIMESTAMP}


		AND ACT_HOURS = #{actHours,jdbcType=DECIMAL}


		AND AMOUNT = #{amount,jdbcType=DECIMAL}


		AND STATUS = #{status,jdbcType=VARCHAR}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND PRICE = #{price,jdbcType=DECIMAL}


  FROM TASK_RECEIVE_EMP_MES


		AND ID = #{id,jdbcType=INTEGER}


		AND INSIDE_ORDER = #{insideOrder,jdbcType=VARCHAR}


		AND PROD_NO = #{prodNo,jdbcType=VARCHAR}


		AND CRAFWORK_ID = #{crafworkId,jdbcType=BIGINT}


		AND DO_EMP_NO = #{doEmpNo,jdbcType=VARCHAR}


		AND ACT_DATE = #{actDate,jdbcType=TIMESTAMP}


		AND PLAN_DATE = #{planDate,jdbcType=TIMESTAMP}


		AND ACT_HOURS = #{actHours,jdbcType=DECIMAL}


		AND AMOUNT = #{amount,jdbcType=DECIMAL}


		AND STATUS = #{status,jdbcType=VARCHAR}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND PRICE = #{price,jdbcType=DECIMAL}

 WHERE DELETED = "F"
